mapper.update:
update board_food
set content = #{content}, phone=#{phone}
where num=#{num}

UPDATE board_food
SET reviewCount=reviewCount+1
WHERE num=#{num}

mapper.select:
select num ,title ,content,phone, addr_x, addr_y, imagePath, categorie,reviewCount, reviewGrade
from board_food
where num=#{num}

SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num ,title ,content,phone, addr_x, addr_y, imagePath, categorie,reviewCount, reviewGrade FROM board_food
ORDER BY num DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}
